import React from "react";
import styled from "styled-components";
import InlineSVG from 'svg-inline-react';
import {Button, ButtonGroup, makeStyles} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
    root: {
        width: "100%",
        position: "relative",
        height: "100%",
        display: "flex",
        alignItems: "center",
        justifyContent: "center"

    },
    buttons: {
        position: 'absolute',
        display: "block",
        left: 10,
        top:5,
        // float: "left"
    },
}));

export default function Display(props) {
    const classes = useStyles();
    const Theme = styled.div`
    svg {
       width: ${(props.theme.size) + "px"};
       fill: ${props.theme.color};
       stroke: ${props.theme.borderColor};
       stroke-width: ${props.theme.borderWidth + "px"}; 

       } 
    `;


    return <div className={classes.root}>
        <ButtonGroup className={classes.buttons} size="small" aria-label="small outlined button group">
            <Button>Display</Button>
            <Button>CSS</Button>
        </ButtonGroup>
        <Theme>
            <InlineSVG src={props.theme.objectFile}/>
        </Theme>
    </div>
}
