import {Col, Container, Row} from "react-bootstrap";
import {Card} from "@material-ui/core";
import Display from "./Display";
import './Visualizer.css';
import Input from "./Input";
import React, {useEffect, useState} from "react";
import Loading from "../Loading";
import {ReactComponent as Svg} from '../../images/fontawesome/solid/apple-alt.svg'

export default function Visualizer() {
    const [inputText, setInputText] = useState("apple");
    const [theme, setTheme] = useState({});
    const [themeLoading, setThemeLoading] = useState(true);

    const [svgObject, setSvgObject] = useState(Svg)

    useEffect(() => {
        setThemeLoading(true);
        fetch("http://localhost:9000/api/convert", {
            method: "POST",
            headers: {
                'Content-Type': 'text/plain'
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            body: inputText
        }).then((data) => data.json())
            .then((json) => {
                setTheme(json);
                import(`${json.objectFile}`)
                    .then((object) => {
                        // setSvgObject(object);
                        console.log(svgObject);
                    })
                setThemeLoading(false);
            })
    }, [inputText, setInputText])


    const changeInput = {setInputText};

    return <Container>
        <Row>
            <Col>
                <Input methods={changeInput}/>
            </Col>
        </Row>
        <Row className="justify-content-center">
            <Col>
                <Card className="displayCard">
                    {themeLoading ? <Loading/> : <Display svg={svgObject} theme={theme}/>}
                </Card>
            </Col>
        </Row>
    </Container>
}
