import React, {useEffect} from "react";
import styled from "styled-components";
import InlineSVG from 'svg-inline-react';

export default function Display(props) {
    const Theme = styled.div`
    svg {
       width: ${(props.theme.size) + "px"};
       fill: ${props.theme.color};
       stroke: ${props.theme.borderColor};
       stroke-width: ${props.theme.borderWidth + "px"}; 

       } 
    `;
    console.log(props.theme)

    let currentSvg = "<svg xmlns=\"http://www.w3.org/2000/svg\" viewBox=\"0 0 512 512\"><!-- Font Awesome Free 5.15.3 by @fontawesome - https://fontawesome.com License - https://fontawesome.com/license/free (Icons: CC BY 4.0, Fonts: SIL OFL 1.1, Code: MIT License) --><path d=\"M256 8C119 8 8 119 8 256s111 248 248 248 248-111 248-248S393 8 256 8z\"/></svg>";

    useEffect(() => {
        currentSvg = props.theme.allObjects[0]
    })

    return <Theme>
        <InlineSVG src={currentSvg} />
    </Theme>
}
